import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Title, TitleYear } from './styles';
import { filmByIdSelector } from '../../redux/selectors/filmSelector';
import { useSelector } from 'react-redux';
import { Card } from 'antd';
import { PlaySquareOutlined, IdcardOutlined, EditOutlined } from '@ant-design/icons';
import './styles.css';
import { Col, Tooltip, Image } from 'antd';
import { PlayerModal } from '../PlayerModal';
import { FilmDetailsModal } from '../FilmDetailsModal';

export const ListFilm = ({ id }) => {
  const state = useSelector((state) => state);
  const film = filmByIdSelector(id)(state);

  const [isPlayerModalVisible, setIsPlayerModalVisible] = useState(false);

  const showPlayerModal = () => {
    setIsPlayerModalVisible(true);
  };
  const hidePlayerModal = () => {
    setIsPlayerModalVisible(false);
  };

  const [isDetailsModalVisible, setIsDetailsModalVisible] = useState(false);

  const showDetailsModal = () => {
    setIsDetailsModalVisible(true);
  };
  const hideDetailsModal = () => {
    setIsDetailsModalVisible(false);
  };

  return (
    film && (
      <Col>
        <Card
          style={{ display: 'flex', maxWidth: '90%', margin: 'auto' }}
          className={'film-card'}
          bodyStyle={{ padding: 0, display: 'flex', flex: 1, flexDirection: 'column' }}
          cover={
            <Image
              fallback='data:image/png;base64,'
              src={film.image}
            />
          }
          hoverable
          actions={[
            <Tooltip key='setting' placement='left' color={'#1c3643'} title='Ver'>
              <PlaySquareOutlined onClick={showPlayerModal} />
            </Tooltip>,
            <Tooltip key='info' placement='top' title='Info'>
              <IdcardOutlined key='info' onClick={showDetailsModal} />
            </Tooltip>,
            <Tooltip key='edit' placement='right' title='Editar'>
              <Link to={`/films/${film.id}`} state={film} className={'edit-link-style'}>
                <EditOutlined key='edit' />
              </Link>
            </Tooltip>,
          ]}
        >
          <TitleYear>
            <Title>{film.filmname + ` (${film.release})`}</Title>
          </TitleYear>
        </Card>
        <PlayerModal
          filmname={film.filmname}
          id={film.id}
          isModalVisible={isPlayerModalVisible}
          handleCancel={hidePlayerModal}
        />
        <FilmDetailsModal film={film} isModalVisible={isDetailsModalVisible} handleCancel={hideDetailsModal} />
      </Col>
    )
  );
};
